function managerFrm(){return{data:{},loading:!1,buttonLabel:"Salvar",loadData(){fetch(document.getElementById("route_list").value).then((t=>t.json())).then((t=>{this.data=t}))},submitData(){if($("#frm").valid()){this.buttonLabel="Aguarde...",this.loading=!0;let t=null!==document.querySelector('input[name="_method"]')?document.querySelector('input[name="_method"]').value:"";this.data._method=t,this.data._token=document.head.querySelector("meta[name=csrf-token]").content,fetch(document.getElementById("frm").action,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(this.data)}).then((t=>{1!=t.ok&&swal("Algo errado!","Se persistir, fale com o administrador!","error"),t.json().then((t=>{swal("Muito bom!","Salvo com sucesso!","success").then((()=>{null!=t.redirect&&(window.location.href=t.redirect)}))}))})).catch((()=>{swal("Error grave!","Se persistir, fale com o administrador!","error")})).finally((()=>{this.loading=!1,this.buttonLabel="Salvar"}))}}}}
